sleep 30

#!/bin/sh

sudo /usr/share/mysql/mysql.server start
sleep 30

#!/bin/sh

# set the MySQL password
/usr/bin/mysql --user=root --password=`cat ~/.mysql_secret | awk '{ print $18; }'` --connect-expired-password <<-EOF
SET PASSWORD = PASSWORD('@@{ROOT_PASSWORD}@@');
FLUSH PRIVILEGES;
EOF

