#!/bin/sh

/usr/bin/mysql --user=root --password=@@{ROOT_PASSWORD}@@ <<-EOF
DELETE FROM mysql.user WHERE User='root' AND Host NOT IN ('localhost', '127.0.0.1', '::1');
DELETE FROM mysql.user WHERE User='@@{APP_DB_USERNAME}@@' AND Host NOT IN ('localhost', '127.0.0.1', '::1');
DELETE FROM mysql.user WHERE User='@@{MONITORING_USERNAME}@@' AND Host NOT IN ('localhost', '127.0.0.1', '::1');
DELETE FROM mysql.user WHERE User='';
GRANT ALL PRIVILEGES ON *.* TO 'root'@'%' WITH GRANT OPTION;
GRANT ALL PRIVILEGES ON *.* TO 'root'@'%' IDENTIFIED BY '@@{ROOT_PASSWORD}@@';
GRANT ALL PRIVILEGES ON *.* TO '@@{APP_DB_USERNAME}@@'@'%' WITH GRANT OPTION;
GRANT ALL PRIVILEGES ON *.* TO '@@{APP_DB_USERNAME}@@'@'%' IDENTIFIED BY '@@{APP_DB_PASSWORD}@@';
GRANT SELECT on sys.* to '@@{MONITORING_USERNAME}@@'@'%';
GRANT SELECT on sys.* to '@@{MONITORING_USERNAME}@@'@'%' IDENTIFIED BY '@@{MONITORING_PASSWORD}@@';
FLUSH PRIVILEGES;
EOF

#!/bin/sh

# create the database specified at app launch
/usr/bin/mysql --user=@@{APP_DB_USERNAME}@@ --password=@@{APP_DB_PASSWORD}@@ --host=127.0.0.1 <<-EOF
CREATE DATABASE IF NOT EXISTS @@{DATABASE_NAME}@@;
GRANT ALL PRIVILEGES ON @@{DATABASE_NAME}@@ TO '@@{APP_DB_USERNAME}@@'@'%' IDENTIFIED BY '@@{APP_DB_PASSWORD}@@';
FLUSH PRIVILEGES;
EOF
sleep 10

# create the database specified at app launch
/usr/bin/mysql --user=@@{APP_DB_USERNAME}@@ --password=@@{APP_DB_PASSWORD}@@ --host=127.0.0.1 <<-EOF
USE @@{DATABASE_NAME}@@;
CREATE TABLE @@{DATABASE_NAME}@@.people (id INT(11) auto_increment, first_name char(50) NOT NULL, last_name char(50) NOT NULL, PRIMARY KEY (id)) ENGINE=NDBCLUSTER;
EOF
sleep 10

#!/bin/sh

sudo ndbd
sleep 30

#!/bin/sh

# set the ProxySQL admin password
/usr/bin/mysql --user=admin --password=admin --host=127.0.0.1 --port=6032 --prompt='ProxySQLAdmin> ' <<-EOF
UPDATE global_variables SET variable_value='admin:@@{PROXYSQL_ADMIN_PASSWORD}@@' WHERE variable_name='admin-admin_credentials';
LOAD ADMIN VARIABLES TO RUNTIME;
SAVE ADMIN VARIABLES TO DISK;
EOF
sleep 10

#!/bin/sh

# set the username that will be used when monitoring nodes in the pool
/usr/bin/mysql --user=admin --password=@@{PROXYSQL_ADMIN_PASSWORD}@@ --host=127.0.0.1 --port=6032 --prompt='ProxySQLAdmin> ' <<-EOF
UPDATE global_variables SET variable_value='@@{MONITORING_USERNAME}@@' WHERE variable_name = 'mysql-monitor-username';
LOAD ADMIN VARIABLES TO RUNTIME;
SAVE ADMIN VARIABLES TO DISK;
EOF
sleep 10

#!/bin/sh

# tell ProxySQL which identifiers it should use for each state (host group)
# 1 = offline
# 2 = writer
# 3 = reader
# 4 = backup writer
/usr/bin/mysql --user=admin --password=@@{PROXYSQL_ADMIN_PASSWORD}@@ --host=127.0.0.1 --port=6032 --prompt='ProxySQLAdmin> ' <<-EOF
INSERT INTO mysql_group_replication_hostgroups (writer_hostgroup, backup_writer_hostgroup, reader_hostgroup, offline_hostgroup, active, max_writers, writer_is_also_reader, max_transactions_behind) VALUES (2, 4, 3, 1, 1, @@{SQL_NODES}@@, 1, 100);
LOAD MYSQL SERVERS TO RUNTIME;
SAVE MYSQL SERVERS TO DISK;
EOF

# add our MySQL nodes to the pool
# grab an array of IP addresses for the SQL nodes
sqlnodes=$(echo "@@{SQLNodeSubstrate.address}@@" | sed 's/^,//' | sed 's/,$//' | tr "," "\n")

for sqlnode in $sqlnodes
do
/usr/bin/mysql --user=admin --password=@@{PROXYSQL_ADMIN_PASSWORD}@@ --host=127.0.0.1 --port=6032 --prompt='ProxySQLAdmin> ' <<-EOF
INSERT INTO mysql_servers(hostgroup_id, hostname, port) VALUES (2, '$sqlnode', 3306);
LOAD MYSQL SERVERS TO RUNTIME;
SAVE MYSQL SERVERS TO DISK;
EOF
sleep 10
done

#!/bin/sh

# set the username that will be used when monitoring nodes in the pool
/usr/bin/mysql --user=admin --password=@@{PROXYSQL_ADMIN_PASSWORD}@@ --host=127.0.0.1 --port=6032 --prompt='ProxySQLAdmin> ' <<-EOF
INSERT INTO mysql_users(username, password, default_hostgroup) VALUES ('@@{APP_DB_USERNAME}@@', '@@{APP_DB_PASSWORD}@@', 2);
LOAD MYSQL USERS TO RUNTIME;
SAVE MYSQL USERS TO DISK;
EOF
sleep 10

